YUI.add("paginator-core",function(t,e){var a=t.namespace("Paginator").Core=function(){};a.ATTRS={page:{value:1},totalPages:{readOnly:!0,getter:"_getTotalPagesFn"},itemsPerPage:{value:10},totalItems:{value:0}},t.mix(a.prototype,{prevPage:function(){return this.hasPrevPage()&&this.set("page",this.get("page")-1),this},nextPage:function(){return this.hasNextPage()&&this.set("page",this.get("page")+1),this},hasPrevPage:function(){return 1<this.get("page")},hasNextPage:function(){return!this.get("totalItems")||this.get("page")<this.get("totalPages")},_getTotalPagesFn:function(){var t=this.get("itemsPerPage");return t<1?1:Math.ceil(this.get("totalItems")/t)}})},"@VERSION@",{requires:["base"]});